// Porfolio.js
import React, { Component } from 'react';
import Modal from './Modal';
import ReactModal from 'react-modal';
import '../path/to/modal.css'; // Import your modal.css file

class Portfolio extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedImage: null,
    };
  }

  handleImageClick = (item) => {
    this.setState({ selectedImage: item });
  };

  handleCloseModal = () => {
    this.setState({ selectedImage: null });
  };

  render() {
    const { resumeData } = this.props;
    const { selectedImage } = this.state;

    return (
      <section id="portfolio">
        <div className="row">
          <div className="twelve columns collapsed">
            <h1>Check Out Some of My Works.</h1>
            <div id="portfolio-wrapper" className="bgrid-quarters s-bgrid-thirds cf">
              {resumeData.portfolio &&
                resumeData.portfolio.map((item) => (
                  <div className="columns portfolio-item" key={item.name}>
                    <div className="item-wrap">
                      <button onClick={() => this.handleImageClick(item)}>
                        <img src={`${item.imgurl}`} className="item-img" alt={item.name} />
                        <div className="overlay">
                          <div className="portfolio-item-meta">
                            <h5>{item.name}</h5>
                            <p>{item.description}</p>
                          </div>
                        </div>
                      </button>
                    </div>
                  </div>
                ))}
            </div>
          </div>
        </div>
        <Modal isOpen={!!selectedImage} onRequestClose={this.handleCloseModal} item={selectedImage} />
      </section>
    );
  }
}

export default Portfolio;
