import React from 'react';
import '../index.css'
const Modal = ({ item }) => {
    return (
      <div id="modal-01" className="modal">
        <div className="modal-content">
          <h2>{item.name}</h2>
          <p>{item.detailedDescription}</p>
          {/* Add any other information you want to display */}
        </div>
      </div>
    );
  };
  
  export default Modal;
